command:
// Because the shader is dynamically compiled,
// intensity is a one time init and can't be updated.
// you must delete the filters to "change" it.
let params =
[{
    filterType: "electric",
    filterId: "myElectric",
    color: 0xFFFFFF,
    time: 0,
    blend: 1,
    intensity: 5,
    animated :
    {
      time : 
      { 
        active: true, 
        speed: 0.0020, 
        animType: "move" 
      }
    }
}];

await TokenMagic.addUpdateFiltersOnSelected(params);
