let glowFunc = async function() {

   const myTokens = canvas.tokens.placeables;

   for (const myToken of myTokens ){
       if (myToken.TMFXhasFilterId("funnyAlternateGlow")) {
           await myToken.TMFXdeleteFilters("funnyAlternateGlow");
       } else {
           let params =
           [{
               filterType: "glow",
               filterId: "funnyAlternateGlow",
               color: Math.floor(Math.random() * 16777215),
               animated: null
           }];
           await myToken.TMFXaddUpdateFilters(params);
       }
   }
};

glowFunc();
